class Youtube {
    constructor(key) {
        this.key = key;
        const getRequestOptions = {
            method: "GET",
            redirect: "follow"
        };
    }
    mostPopular() {
        return fetch("https://www.googleapis.com/youtube/v3/videos?part=snippet&chart=mostPopular&maxResults=25&key=AIzaSyC2AT4GuUBTgMwQckU1UpGSA5nLWfH3J64", //
            this.getRequestOptions) //.then((response) => response.json()) //.then((result) => result.items)
    }

    search(query) {
        return fetch(`https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=${query}&type=video&key=AIzaSyC2AT4GuUBTgMwQckU1UpGSA5nLWfH3J64`, this.getRequestOptions).then((response) => response.json()).then((result) => result.items.map((item) => ({
            ...item,
            id: item.id.videoId
        }))).then((items) => items)
    }
}
export default Youtube;
