import React, { useState, useEffect } from "react";
import styles from "./app.module.css";
import VideoList from "./compoenets/video_list/video_list";
import Video_search from "./compoenets/video_search/video_search";

function App() {
  const [videos, setVideos] = useState([]);
  const search = (query) => {
    const requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      `https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=${query}&type=video&key=AIzaSyC2AT4GuUBTgMwQckU1UpGSA5nLWfH3J64`,
      requestOptions
    )
      .then((response) => response.json())
      .then((result) =>
        result.items.map((item) => ({ ...item, id: item.id.videoId }))
      )
      .then((items) => setVideos(items))
      .catch((error) => console.log("error", error));
  };

  useEffect(() => {
    console.log("useEffect");
    const requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://www.googleapis.com/youtube/v3/videos?part=snippet&chart=mostPopular&maxResults=25&key=AIzaSyC2AT4GuUBTgMwQckU1UpGSA5nLWfH3J64",
      requestOptions
    )
      .then((response) => response.json())
      .then((result) => setVideos(result.items))
      .catch((error) => console.log("error", error));
  }, []);
  return (
    <div className={styles.app}>
      <Video_search onSearch={search} />
      <VideoList videos={videos} />;
    </div>
  );
}

export default App;
